"use strict";$(function(){var n=location.search.split("=")[1];$.getJSON("http://datainfo.duapp.com/shopdata/getGoods.php?callback=?",{classID:n},function(n){console.log(n);var i="";$.each(n,function(n,s){i+='\n            <li class="item price_list2">\n                <div class="p-item">\n                    <div class="p-img">\n                        <a href="products.html?id='+s.goodsID+'" target="_blank">\n                            <img src="'+s.goodsListImg+'">\n                        </a>\n                    </div>\n                    <div class="title-a">\n                        <a href="products.html?id='+s.goodsID+'" target="_blank">'+s.goodsName+'</a>\n                    </div>\n                    <div class="p-price">\n                        <span class="p-now">￥ <strong>'+s.price+'</strong></span>\n                    </div>\n                    <div class="p-btn">\n                        <a>加入购物车</a>\n                    </div>\n                </div>\n            </li>'}),$("#floorProduct237").html(i),$(".price_list2").mouseenter(function(){var n=$(this).index();$(".p-btn").eq(n).show()}),$(".price_list2").mouseleave(function(){var n=$(this).index();$(".p-btn").eq(n).hide()})})});